function [monitor] = SimCalToMonitor(fileName)% [monitor] = SimCalToMonitor(fileName)%% Reads a psychtoolbox monitor description from the file and fill in the structure.% for simulator toolbox use.%% 8/15/03  dhb  Wrote it.% Copyright (c) 2003 David Brainard.   All rights reserved.% Argument checkif (nargin < 1)	  error('SimCalToMonitor: requires a cal file');end% Open the filemonitor = LoadCalFile(fileName);% Fill in some SimToolbox fields that aren't typically in the Psychtoolbox% cal files.monitor.manufacturer = 'Psychtoolbox Calibration';monitor.name = monitor.describe.monitor;monitor.wavelengthSampling = MakeItStruct(monitor.S_device);